import React, { useState } from 'react'
import { Link, useNavigate } from 'react-router-dom'

const CreateStudent = () => {
  const[id,setId]=useState("")
  const[name,setName]=useState("")
  const[place,setPlace]=useState("")
  const[phone,setPhone]=useState("")
  const[validation,setValidation]=useState(false)
  const navigate=useNavigate()


  const handleSubmit=(e)=>{
    e.preventDefault();
    const studentData={id,name,place,phone}
    console.log(studentData);

    fetch("http://localhost:8000/students",{
      method:'POST',
      headers:{
        "content-type":"application/json"
      },
      body:JSON.stringify(studentData)

    })
    .then((res)=>{
      alert("Student Data Saved Successfully")
      navigate("/")
    })
    .catch((err)=>console.log(err.message))
  } 


  return (
    <div className='container'>
      <h2>Add New Student</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor='id'>ID: </label>
        <input type='text' id='id' name='id' onChange={e=>setId(e.target.value)} value={id} required onMouseDown={()=>setValidation(true)} />
        {id.length===0 &&  validation && <span className='errMsg'>Please Enter Your Id </span>}<br/>

        <label htmlFor='name'>Name:</label>
        <input type='text' id='name' name='name' onChange={e=>setName(e.target.value)} value={name} required onMouseDown={()=>setValidation(true)}/>
        {name.length===0 &&  validation && <span className='errMsg'>Please Enter Your Name </span>}


        <label htmlFor='place'>Place:</label>
        <input type='text' id='place' name='place' onChange={e=>setPlace(e.target.value)} value={place} onMouseDown={()=>setValidation(true)}/>
        {place.length===0 && validation && <span className='errMsg'>Please Enter Your Place </span>}


        <label htmlFor='phone'>Phone:</label>
        <input type='text' id='phone' name='phone' onChange={e=>setPhone(e.target.value)} value={phone} onMouseDown={()=>setValidation(true)}/>
        {phone.length===0 && validation && <span className='errMsg'>Please Enter Your Phone </span>}


        <div>
          <button className='btn btn-primary'>Save</button>
          <Link to='/' className='btn btn-danger'>Back</Link>        

        </div>

      </form>

      
    </div>
  )
}

export default CreateStudent
